const initialState = {
  file: null,
  message: "",
};

const fileReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_FILE":
      return { ...state, file: action.payload };
    case "SET_MESSAGE":
      return { ...state, message: action.payload };
    default:
      return state;
  }
};

export default fileReducer;
