// store/actions.js
export const setInvoices = (invoices) => ({
  type: "SET_INVOICES",
  payload: invoices,
});

export const setProducts = (products) => ({
  type: "SET_PRODUCTS",
  payload: products,
});

export const setCustomers = (customers) => ({
  type: "SET_CUSTOMERS",
  payload: customers,
});
