import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setInvoices } from '../store/slices/dataSlice';

const TabsComponent = () => {
  const invoices = useSelector((state) => state.data.invoices);
  const dispatch = useDispatch();

  const addInvoice = () => {
    const newInvoice = {
      id: invoices.length + 1,
      customerName: 'John Doe',
      totalAmount: 500,
    };
    dispatch(setInvoices([...invoices, newInvoice]));
  };

  return (
    <div>
      <h3>Invoices</h3>
      <button onClick={addInvoice}>Add Invoice</button>
      <ul>
        {invoices.map((invoice) => (
          <li key={invoice.id}>
            {invoice.customerName} - ${invoice.totalAmount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TabsComponent;
