// reducer.js

import { SET_MESSAGE } from "./actions";

// Initial state
const initialState = {
  message: "",
};

// Reducer function
const messageReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_MESSAGE:
      return {
        ...state,
        message: action.payload,
      };
    default:
      return state;
  }
};

export default messageReducer;
