import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setFile, setMessage, uploadFile } from '../redux/actions';
import pdfParse from 'pdf-parse';

const FileUpload = () => {
  const dispatch = useDispatch();
  const { file, message } = useSelector((state) => state);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      dispatch(setFile(uploadedFile));
      dispatch(setMessage(`File "${uploadedFile.name}" selected.`));
    } else {
      dispatch(setMessage('No file selected.'));
    }
  };

  const handleFileUpload = () => {
    if (file) {
      dispatch(setMessage(`Uploading "${file.name}"...`));

      // Simulate file upload
      setTimeout(() => {
        dispatch(setMessage(`"${file.name}" uploaded successfully!`));

        // If it's a PDF, you can extract text here
        if (file.type === 'application/pdf') {
          const reader = new FileReader();
          reader.onload = () => {
            const fileBuffer = reader.result;
            pdfParse(fileBuffer)
              .then((data) => {
                dispatch(setMessage(`PDF Text Extracted: ${data.text.substring(0, 200)}`)); // Show a snippet of extracted text
              })
              .catch((error) => {
                dispatch(setMessage(`Error parsing PDF: ${error.message}`));
              });
          };
          reader.readAsArrayBuffer(file);
        }
      }, 1500); // Simulate upload delay
    } else {
      dispatch(setMessage('Please select a file to upload.'));
    }
  };

  return (
    <div className="card p-4" style={{ borderRadius: '15px', backgroundColor: '#f8f9fa' }}>
      <h4 className="text-center mb-3">File Upload</h4>
      <div className="mb-3">
        <input
          type="file"
          className="form-control"
          onChange={handleFileChange}
        />
      </div>
      <button
        className="btn btn-primary w-100"
        onClick={handleFileUpload}
      >
        Upload File
      </button>
      {message && <div className="alert alert-info mt-3">{message}</div>}
    </div>
  );
};

export default FileUpload;
