import { configureStore } from '@reduxjs/toolkit';
import thunk from 'redux-thunk'; // Middleware for handling async actions
import rootReducer from './reducer'; // Ensure correct path

const store = configureStore({
  reducer: rootReducer,
  middleware: [thunk],
  devTools: process.env.NODE_ENV !== 'production', // Enables Redux DevTools in non-production environments
});

export default store;
