import React from 'react';
import { useDispatch } from 'react-redux';
import { setMessage } from '../store/actions'; // Correct path
import * as pdfjsLib from 'pdfjs-dist';  // Import the full pdfjs-dist library

// Set the worker for pdfjs
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const FileUpload = () => {
  const dispatch = useDispatch();

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      dispatch(setMessage(`File "${uploadedFile.name}" selected.`));
      handlePdfParse(uploadedFile);
    } else {
      dispatch(setMessage('No file selected.'));
    }
  };

  const handleFileUpload = () => {
    dispatch(setMessage('Uploading file...'));
    // Add logic for actual file upload if needed
  };

  const handlePdfParse = (file) => {
    const fileReader = new FileReader();

    fileReader.onload = () => {
      const typedArray = new Uint8Array(fileReader.result);

      // Use pdfjs to parse the file
      pdfjsLib.getDocument(typedArray).promise
        .then((pdf) => {
          // Get text from the first page
          pdf.getPage(1).then((page) => {
            page.getTextContent().then((textContent) => {
              let text = '';
              textContent.items.forEach(item => {
                text += item.str + ' ';
              });

              // Here, you can handle the extracted text (e.g., save to store or show in UI)
              dispatch(setMessage(`PDF text: ${text.slice(0, 100)}...`));  // Show first 100 characters as an example
            });
          });
        })
        .catch((error) => {
          dispatch(setMessage(`Error parsing PDF: ${error.message}`));
        });
    };

    fileReader.readAsArrayBuffer(file);
  };

  return (
    <div className="card p-4" style={{ borderRadius: '15px', backgroundColor: '#f8f9fa' }}>
      <h4 className="text-center mb-3">File Upload</h4>
      <div className="mb-3">
        <input
          type="file"
          className="form-control"
          onChange={handleFileChange}
        />
      </div>
      <button
        className="btn btn-primary w-100"
        onClick={handleFileUpload}
      >
        Upload File
      </button>
    </div>
  );
};

export default FileUpload;
