import React, { useRef } from 'react';
import { Container, Navbar, Nav } from 'react-bootstrap';
import TabsComponent from './components/Tabs';
import FileUpload from './components/FileUpload';

const App = () => {
  const invoicesRef = useRef(null);
  const productsRef = useRef(null);
  const customersRef = useRef(null);

  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="#home">Invoice Management</Navbar.Brand>
        <Nav className="ml-auto">
          <Nav.Link onClick={() => scrollToSection(invoicesRef)}>Invoices</Nav.Link>
          <Nav.Link onClick={() => scrollToSection(productsRef)}>Products</Nav.Link>
          <Nav.Link onClick={() => scrollToSection(customersRef)}>Customers</Nav.Link>
        </Nav>
      </Navbar>

      <Container className="mt-4">
        <div ref={invoicesRef}>
          <h3>Invoices Section</h3>
          <TabsComponent />
        </div>

        <div ref={productsRef} className="mt-5">
          <h3>Products Section</h3>
          {/* Products component or content can be added here */}
        </div>

        <div ref={customersRef} className="mt-5">
          <h3>Customers Section</h3>
          {/* Customers component or content can be added here */}
        </div>

        <FileUpload />
      </Container>
    </div>
  );
};

export default App;
