// reducer.js

import { SET_MESSAGE } from "./actions";

const initialState = {
  parsedPdfData: '',
  message: '',
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_PARSED_PDF_DATA':
      return {
        ...state,
        parsedPdfData: action.payload,
      };
    case 'SET_MESSAGE':
      return {
        ...state,
        message: action.payload,
      };
    default:
      return state;
  }
};

export default reducer;
